// Get all binary keys from the webhook
const binaryData = $('Webhook').item.binary || {};
const imageKeys = Object.keys(binaryData);

// Create output items for each image
const outputItems = [];

for (let i = 0; i < imageKeys.length; i++) {
  const imageKey = imageKeys[i];

  // Create new item with the binary data and metadata
  outputItems.push({
    json: {
      current_image_key: imageKey,
      image_index: i,
      total_images: imageKeys.length
    },
    binary: {
      [imageKey]: binaryData[imageKey]
    }
  });
}

return outputItems;

